def parse_expansion(expansion):
   if "|" in expansion:
       (op, arg, vars_) = expansion.split("|")
       op = op[1:]
   else:
       (op, arg, vars_) = (None, None, expansion)
   vars_ = vars_.split(",")

   variables = {}
   for var in vars_:
       if "=" in var:
           (varname, vardefault) = var.split("=")
       else:
           (varname, vardefault) = (var, None)
       variables[varname] = vardefault

   return (op, arg, variables)
